import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix default icon paths for leaflet (required when using webpack/create-react-app)
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

function getToken() {
  return localStorage.getItem('token');
}

function PresensiPage() {
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [coords, setCoords] = useState(null); // { lat, lng }

  // Ambil lokasi pengguna
  const getLocation = () => {
    setError('');
    if (!navigator.geolocation) {
      setError('Geolocation tidak didukung oleh browser ini.');
      console.error('navigator.geolocation is undefined');
      return;
    }

    const options = { enableHighAccuracy: true, timeout: 20000, maximumAge: 0 };

    const requestPosition = () => {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          if (watchRef.current) {
            navigator.geolocation.clearWatch(watchRef.current);
            watchRef.current = null;
          }
          setCoords({ lat: position.coords.latitude, lng: position.coords.longitude });
          setError('');
          console.log('Got position', position);
        },
        (err) => {
          console.error('getCurrentPosition error', err);
          // Provide clearer messages based on error code
          if (err.code === 1) {
            setError('Akses lokasi ditolak. Mohon izinkan akses lokasi di pengaturan browser.');
          } else if (err.code === 2) {
            setError('Posisi tidak tersedia saat ini (POSITION_UNAVAILABLE). Mencoba metode alternatif...');
            // fallback to watchPosition
            try {
              const id = navigator.geolocation.watchPosition(
                (position) => {
                  if (watchRef.current) {
                    navigator.geolocation.clearWatch(watchRef.current);
                    watchRef.current = null;
                  }
                  setCoords({ lat: position.coords.latitude, lng: position.coords.longitude });
                  setError('');
                  console.log('Got position from watchPosition', position);
                },
                (watchErr) => {
                  console.error('watchPosition error', watchErr);
                  setError('Gagal mendapatkan lokasi: ' + watchErr.message + ' (' + (watchErr.code || '') + ')');
                },
                { enableHighAccuracy: true, timeout: 30000, maximumAge: 0 }
              );
              watchRef.current = id;
            } catch (watchEx) {
              console.error('watchPosition throw', watchEx);
              setError('Gagal memulai watchPosition: ' + watchEx.message);
            }
          } else if (err.code === 3) {
            setError('Permintaan lokasi melebihi batas waktu. Coba lagi.');
          } else {
            setError('Gagal mendapatkan lokasi: ' + err.message);
          }
        },
        options
      );
    };

    // If Permissions API exists, check state to give better guidance
    if (navigator.permissions && navigator.permissions.query) {
      try {
        navigator.permissions.query({ name: 'geolocation' }).then((status) => {
          console.log('geolocation permission state', status.state);
          if (status.state === 'denied') {
            setError('Akses lokasi diblokir. Periksa pengaturan privasi pada browser atau sistem operasi.');
          } else {
            requestPosition();
          }
        }).catch((e) => {
          console.warn('permissions.query failed', e);
          requestPosition();
        });
      } catch (e) {
        requestPosition();
      }
    } else {
      requestPosition();
    }
  };

  const watchRef = useRef(null);

  useEffect(() => {
    getLocation();
  }, []);

  const handleCheckIn = async () => {
    setError('');
    setMessage('');
    try {
      const config = {
        headers: {
          Authorization: `Bearer ${getToken()}`,
        },
      };
      if (!coords) {
        setError('Lokasi belum didapatkan. Mohon izinkan akses lokasi.');
        return;
      }

      const response = await axios.post(
        '/api/presensi/check-in',
        {
          latitude: coords.lat,
          longitude: coords.lng,
        },
        config
      );
      setMessage(response.data.message || 'Check-in berhasil');
    } catch (err) {
      setError(err.response ? (err.response.data.message || JSON.stringify(err.response.data)) : 'Check-in gagal');
    }
  };

  const handleCheckOut = async () => {
    setError('');
    setMessage('');
    try {
      const config = {
        headers: {
          Authorization: `Bearer ${getToken()}`,
        },
      };
      const response = await axios.post('/api/presensi/check-out', {}, config);
      setMessage(response.data.message || 'Check-out berhasil');
    } catch (err) {
      setError(err.response ? (err.response.data.message || JSON.stringify(err.response.data)) : 'Check-out gagal');
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center">
      {coords && (
        <div className="my-4 border rounded-lg overflow-hidden w-full max-w-md">
          <MapContainer center={[coords.lat, coords.lng]} zoom={15} style={{ height: '300px', width: '100%' }}>
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            />
            <Marker position={[coords.lat, coords.lng]}>
              <Popup>Lokasi Presensi Anda</Popup>
            </Marker>
          </MapContainer>
        </div>
      )}
      <div className="bg-white p-8 rounded-lg shadow-md w-full max-w-md text-center">
        <h2 className="text-3xl font-bold mb-6 text-gray-800">Lakukan Presensi</h2>

        {message && <p className="text-green-600 mb-4">{message}</p>}
        {error && <p className="text-red-600 mb-4">{error}</p>}

        {coords && (
          <p className="text-sm text-gray-600 mb-4">Lokasi saat ini: {coords.lat.toFixed(6)}, {coords.lng.toFixed(6)}</p>
        )}

        {!coords && (
          <p className="text-sm text-gray-500 mb-4">Sedang mengambil lokasi...</p>
        )}

        <div className="mb-4">
          <button onClick={getLocation} className="text-sm text-blue-600 underline">Ambil lokasi ulang</button>
        </div>

        <div className="flex space-x-4">
          <button
            onClick={handleCheckIn}
            className="w-full py-3 px-4 bg-green-600 text-white font-semibold rounded-md shadow-sm hover:bg-green-700"
          >
            Check-In
          </button>

          <button
            onClick={handleCheckOut}
            className="w-full py-3 px-4 bg-red-600 text-white font-semibold rounded-md shadow-sm hover:bg-red-700"
          >
            Check-Out
          </button>
        </div>
      </div>
    </div>
  );
}

export default PresensiPage;
